                    $this->checkBrowserWebTv() ||
                    $this->checkBrowserInternetExplorer() ||
                    $this->checkBrowserOpera() ||
                    $this->checkBrowserGaleon() ||
                    $this->checkBrowserNetscapeNavigator9Plus() ||
