                    $this->checkBrowserFirebird() ||
                    $this->checkBrowserKonqueror() ||
                    $this->checkBrowserIcab() ||
                    $this->checkBrowserPhoenix() ||
                    $this->checkBrowserAmaya() ||
                    $this->checkBrowserLynx() ||

                    $this->checkBrowserShiretoko() ||
