class wtd_custom_page{

    public function __construct(){

        //Custom Page
        $wtd_feed = get_option('wtd_feed');
        if ($wtd_feed)
            add_action('add_meta_boxes',array(&$this,'wtd_custom_page_meta_box'));

        //Save Post
        add_action('save_post',array(&$this,'save_post'));

        //Page Content
        add_action('the_content', array(&$this, 'render'), 99);

        //Ajax calls
        add_action('wp_ajax_wtd_custom_page_results',array(&$this,'wtd_custom_page_results'));
        add_action('wp_ajax_nopriv_wtd_custom_page_results',array(&$this,'wtd_custom_page_results'));

        add_filter('the_content','wpautop');
    }

    public function wtd_custom_page_meta_box($post_type){
        $wtd_pages = get_option('wtd_pages');
        $is_generated = false;
        global $post;
        foreach($wtd_pages as $key => $page){
            if (is_array($page) && $key != 'custom_pages')
                if (in_array($post->ID,$page)){
                    $is_generated = true;
                    break;
                }
        }
        if (in_array($post_type,array('post','page')) && !$is_generated){
            add_meta_box(
                'wtd_meta_box',
                'Super Advanced What To Do Page Builder',
                array( &$this, 'meta_box' ),
                $post_type,
                'advanced',
                'high'
            );

        }

    }

