    public function save_post($post_id){
        if (!empty($_POST['post_type'])){
            $post_type = $_POST['post_type'];
            if (in_array($post_type,array('post','page'))){
                $options = get_option('wtd_pages');
                if (!empty($_POST['wtd_page_options'])){
                    if (empty($_POST['wtd_page_options']['wtd_terms'])){
                        if(!empty($_POST['wtd_page_options']['wtd_page']))
                            unset($_POST['wtd_page_options']['wtd_page']);
                        if (!empty($options['custom_pages'])){
                            if ($key = array_search($post_id,$options['custom_pages']))
                                unset($options['custom_pages'][$key]);
                        }
                    }elseif(!empty($_POST['wtd_page_options']['wtd_page'])){
                        if (!empty($options['custom_pages'])){
                            if (!in_array($post_id,$options['custom_pages']))
                                $options['custom_pages'][] = $post_id;
                        }else{
                            $options['custom_pages'] = array($post_id);
                        }
                    }
                    update_post_meta($post_id,'wtd_page_options',$_POST['wtd_page_options']);
                    if(!empty($_POST['wtd_page_options']['wtd_page']))
                        update_post_meta($post_id,'wtd_page','custom_page');
                    elseif ('custom_page' == get_post_meta($post_id,'wtd_page',true))
                        delete_post_meta($post_id, 'wtd_page');
                }else{
                    if (!empty($options['custom_pages']))
                        if ($key = array_search($post_id,$options['custom_pages']))
                            unset($options['custom_pages'][$key]);
                    $meta = get_post_meta($post_id,'wtd_page',true);
                    if ('custom_page' == $meta)
                        delete_post_meta($post_id,'wtd_page');

                }
                if(empty($_POST['wtd_page_options']['wtd_page'])) {
                    if (!empty($options['custom_pages']))
                        if ($key = array_search($post_id,$options['custom_pages']))
                            unset($options['custom_pages'][$key]);
                    $meta = get_post_meta($post_id,'wtd_page',true);
                    if ('custom_page' == $meta)
                        delete_post_meta($post_id, 'wtd_page');
                }

                update_option('wtd_pages',$options);
            }
        }
    }

    public function render($content){

        if(!is_singular('page') || !in_the_loop())
            return $content;

        global $wpdb;
        global $wtd_plugin;
        global $post;
        global $wp_query;
        if (in_array($post->post_type,array('post','page'))){
            $options = get_post_meta($post->ID,'wtd_page_options',true);
            if (!empty($options['wtd_page'])){

                remove_filter('the_content', 'theme_formatter', 99);
                remove_filter ('the_content', 'wpautop', 99);

                $page_content = '';
                $exclude = array();
                if (!empty($options['excluded_categories']))
                    foreach ($options['excluded_categories'] as $cat){
                        $exclude[] = $cat;
                    }

                $not_allowed = $wpdb->get_results(
                    'SELECT term_id FROM '.$wpdb->prefix.'wtd_meta WHERE meta_key = "cat_id" AND wtd_term_id IN (6,903,1008) LIMIT 3'
                );
                foreach ($not_allowed as $n)
                    $exclude[] = $n->term_id;

                $categories = get_terms('wtd_category',array('parent' => 0,'post_type' =>$options['wtd_terms'],'exclude' => $exclude,'cache_domain' => 'wtd_custom_'.$post->ID,'resorts' => $options['resorts']));
                switch($options['page_style']):

                    case 1:
                        $page_content = $this->big_menu($content);

                        break;

                    case 2:
                        if (empty($wp_query->query['wtdc']) && count($categories) > 1)
                            $page_content = $this->left_side_menu($content);
                        else
                            $page_content = $this->results($content);

                        break;
                    case 3:
                        if (empty($wp_query->query['wtdc']) && count($categories) > 1)
                            $page_content = $this->left_side_menu($content);
                        else
                            $page_content = $this->results($content);
                        break;

                endswitch;
                if (!empty($options['display'])){
                    switch($options['display']):

                        case 1: $content = $page_content;break;
                        case 2: $content .= $page_content;break;
                        case 3: $content = $page_content.$content;break;

                    endswitch;
                }else
                    $content = $page_content;

            }else{
                add_filter('the_content', 'wpautop');
            }

        }

        return $content;
    }
