    private function results($content){
        global $wpdb;
        global $wp_query;
        global $post;
        if (!empty($wp_query->query['wtdc']))
            $category = get_term_by('slug',$wp_query->query['wtdc'],'wtd_category');
        $options = get_post_meta($post->ID,'wtd_page_options',true);
        $exclude = array();
        if (!empty($options['excluded_categories']))
            foreach ($options['excluded_categories'] as $cat){
                $exclude[] = $cat;
            }
        $not_allowed = $wpdb->get_results(
            'SELECT term_id FROM '.$wpdb->prefix.'wtd_meta WHERE meta_key = "cat_id" AND wtd_term_id IN (6,903,1008) LIMIT 3'
        );
        foreach ($not_allowed as $n)
            $exclude[] = $n->term_id;
        $parent_cats = get_terms('wtd_category',array('parent' => 0,'post_type' =>$options['wtd_terms'],'exclude' => $exclude,'cache_domain' => 'wtd_custom_'.$post->ID,'resorts' => $options['resorts']));
        if (empty($category))
            foreach($parent_cats as $p) {
                $category = $p;
                break;
            }
        ob_start();
        $masonry_item = '';
        if ($category && !is_wp_error($category)):
            ?>
            <link rel="stylesheet" href="<?php echo WTD_PLUGIN_URL.'assets/css/wtd_activities_page.css?wtd_version='.WTD_VERSION; ?>" media="screen"/>
            <?php if (count($parent_cats) > 1 || $options['page_style'] != 3): ?>
            <div id="wtd_breadcrumb">
                <a id="parent_<?php echo $post->ID;?>_header" href="<?php echo get_post_permalink($post->ID);?>" style="float:left;"><?php
                    echo $post->post_title;?>
                </a>
                <span class="wtd_bread_separator">&gt;</span>
                <?php
                $first_cat = $category;
                if ($category->parent)
                    $first_cat = get_term($category->parent,'wtd_category');
                if ($options['page_style'] == 2):?>
                    <select class="wtd_subcategory_navigator">
                        <option>Select Category</option>
                        <?php
                        foreach ($parent_cats as $scat){
                            $selected = '';
                            if ($scat->term_id == $first_cat->term_id)
                                $selected = ' selected="selected"';
                            echo '<option data-slug="'.$scat->slug.'"'.$selected.'>'.$scat->name.'</option>';
                        }
                        ?>
                    </select>
                <?php else: ?>
                    <a id="parent_<?php echo $first_cat->term_id;?>_header" href="<?php echo get_post_permalink($post->ID).'category/'.$first_cat->slug; ?>" class="wtd_pull_left"><?php
                        echo $first_cat->name;?>
                    </a>
                <?php endif;?>
                <?php if ($options['page_style'] == 2):
                    $parent_category = $category->term_id;
                    if ($category->parent)
                        $parent_category = $category->parent;
                    ?>
                    <span class="wtd_bread_separator">&gt;</span>
                    <select class="wtd_subcategory_navigator">
                        <option>Select Subcategory</option>
                        <?php
                        $child_cats = get_terms('wtd_category',array('parent' => $parent_category,'post_type' =>$options['wtd_terms'],'exclude' => $exclude,'cache_domain' => 'wtd_custom_'.$post->ID,'resorts' => $options['resorts']));
                        foreach ($child_cats as $scat){
                            $selected = '';
                            if ($scat->term_id == $category->term_id)
                                $selected = ' selected="selected"';
                            echo '<option data-slug="'.$scat->slug.'"'.$selected.'>'.$scat->name.'</option>';
                        }
                        ?>
                    </select>
                <?php elseif ($category->parent): ?>
                    <span class="wtd_bread_separator">&gt;</span>
                    <a id="parent_<?php echo $category->term_id;?>_header" href="<?php echo get_post_permalink($post->ID).'category/'.$category->slug; ?>"  class="wtd_pull_left"><?php
                        echo $category->name;?>
                    </a>
                <?php endif;?>
            </div>
            <script>
                jQuery('.wtd_subcategory_navigator').change(function(){
                    var slug = jQuery(this).find('option:selected').attr('data-slug');
                    if (slug)
                        window.location = "<?php echo get_post_permalink($post->ID).'category/';?>" + slug;
                });
            </script>
        <?php endif; ?>
            <?php
            if ($options['page_style'] == 3):
                $masonry_item = 'wtd_page_results';
                $wtd_parent_id = $category->parent ? $category->parent : $category->term_id;

                $term_args = array('parent' => $wtd_parent_id,'post_type' => $options['wtd_terms']);
                if (!empty($options['resorts']))
                    $term_args['resorts'] = $options['resorts'];
                if (!empty($exclude))
                    $term_args['exclude'] = $exclude;
                $subcategories = get_terms('wtd_category',$term_args);
                ?>
                <ul class="wtd_subcategory_menu">
                    <?php foreach ($subcategories as $scat): ?>
                        <li class="wtd_subcategory_menu_item <?php echo ($scat->slug == $wp_query->query['wtdc']) ? 'active' : '';?>">
                            <a href="<?php echo get_post_permalink($post->ID).'category/'.$scat->slug; ?>"><?php echo $scat->name;?></a>
                        </li>
                    <?php endforeach; ?>
                </ul>
            <?php endif;?>
            <div id="wtd_listing_sc_container">
                <?php
                $paged = !empty($_GET['pagenr']) ? $_GET['pagenr'] : 1;
                $args = array(
                    'posts_per_page' => 10,
                    'paged' => $paged,
                    'post_type' => $options['wtd_terms'],
                    'tax_query' => array(
                        array(
                            'taxonomy' => 'wtd_category',
                            'field' => 'slug',
                            'terms' => array($category->slug),
                            'operator' => 'IN'
                        )
                    ),
                    'meta_query' => array()
                );
                if (!empty($options['resorts'])){
                    $args['tax_query'][] = array(
                        'taxonomy' => 'wtd_resort',
                        'field' => 'id',
                        'terms' => $options['resorts'],
                        'operator' => 'IN'
                    );
                }
                if (!empty($options['locations'])){
                    $args['tax_query'][] = array(
                        'taxonomy' => 'wtd_location',
                        'field' => 'id',
                        'terms' => $options['locations'],
                        'operator' => 'NOT IN'
                    );
                }
                switch($options['providers_type']):

                    case 2:
                        $args['meta_query'][] = array(
                            'key' => 'vend_id',
                            'value' => $options['providers'],
                            'compare' => 'IN'
                        );
                        break;
                    case 3:
                        $args['meta_query'][] = array(
                            'key' => 'vend_id',
                            'value' => $options['excluded_providers'],
                            'compare' => 'NOT IN'
                        );
                        break;

                endswitch;
                $query = new WP_Query($args);
                $pagination = false;
                if ($query->max_num_pages > 1){
                    $pagination =paginate_links( array(
                        'total' => $query->max_num_pages,
                        'format' => '?pagenr=%#%',
                        'current' => $paged,
                        'show_all' => false,
                        'end_size' => 1,
                        'mid_size' => 2,
                        'prev_next' => true,
                        'prev_text' => __('&laquo; Previous'),
                        'next_text' => __('Next &raquo;'),
                        'type' => 'plain',
                        'add_args' => false,
                        'add_fragment' => NULL
                    ));
                }
                if($query->found_posts):
                    $i = 0;
                    foreach ($query->posts as $result):
                        $vendor_metas = $wpdb->get_results(
                            $wpdb->prepare(
                                'SELECT pm.*,p.post_title as vendor FROM '.$wpdb->prefix.'postmeta pm
								JOIN '.$wpdb->prefix.'postmeta pmm ON pmm.post_id = %s AND pmm.meta_key = "vend_id"
								JOIN '.$wpdb->prefix.'wtd_meta wm ON wm.meta_key = "vend_id" AND wm.wtd_term_id = pmm.meta_value
								JOIN '.$wpdb->prefix.'posts p ON p.ID = wm.term_id
							WHERE
								pm.post_id = wm.term_id',
                                $result->ID

                            )
                        );
                        foreach ($vendor_metas as $vend_data){
                            $name = $vend_data->meta_key;
                            $$name = $vend_data->meta_value;
                            $vendor = $vend_data->vendor;
                        }
                        $metas = $wpdb->get_results($wpdb->prepare('SELECT * FROM '.$wpdb->postmeta.' WHERE post_id = %s',$result->ID));
                        foreach ($metas as $key => $meta){
                            $name = $meta->meta_key;
                            $$name = maybe_unserialize($meta->meta_value);
                        }
                        switch ($result->post_type):

                            case 'wtd_activity':
                                $image = get_post_meta($result->ID,'listing_thm',true);
                                $image = wtd_get_image_url($image);
                                break;

                            case 'wtd_coupon':
                                $image = get_post_meta($result->ID,'coup_thm',true);
                                $image = wtd_get_image_url($image);
                                break;

                        endswitch;
                        ?>
                        <div class="wtd_listing_container <?php echo $masonry_item;?>">
                            <div class="wtd_listing_sc_listing_list">
                                <div class="wtd_listing_sc_top_content clearfix">
                                    <?php
                                    $listing_url = get_post_permalink($result->ID);
                                    if (!empty($result->post_title))
                                        $listing_title = $result->post_title . ' - ' . $vendor;
                                    if (!empty($result->post_title) && !empty($vendor))
                                        $listing_title .= ' - ';
                                    if (!empty($vendor))
                                        $listing_title .= $vendor;
                                    ?>
                                    <p class="wtd_listing_title_bar"><a href="<?php echo $listing_url;?>"><?php echo $listing_title; ?></a></p>
                                    <div class="wtd_listing_sc_imageArea">
                                        <img src="<?php echo $image; ?>" alt="Vendor Image"/>
                                    </div>
                                    <div class="wtd_listing_sc_centerContent">
                                        <ul>
                                            <li>
                                                <p><?php
                                                    $desc = $result->post_content;
                                                    wtd_excerpt_generator($desc);
                                                    ?>
                                                </p>
                                            </li>
                                        </ul>
                                        <?php if ($result->post_type == 'wtd_activity'): ?>
                                            <div class="result_address">

                                                <?php
                                                if (count($addresses) == 1):
                                                    $display_address = '';
                                                    $street = $addresses[0]['street'];
                                                    if (!empty($street))
                                                        $display_address .= $street;

                                                    $city = $addresses[0]['city'];
                                                    if (!empty($city)){
                                                        if (!$display_address)
                                                            $display_address .= $city;
                                                        else
                                                            $display_address .= ' in '.$city;
                                                    }

                                                    $state = $addresses[0]['state'];
                                                    if (!empty($state)){
                                                        if (empty($display_address))
                                                            $display_address .= $state;
                                                        else
                                                            $display_address .= ', '.$state;
                                                    }

                                                    $phone = $listing_phone;
                                                    if (empty($listing_phone))
                                                        $phone = $vend_phone;
                                                    if (!empty($phone)){
                                                        $display_address .= " (".substr($phone, 0, 3).") ".substr($phone, 3, 3)."-".substr($phone,6);
                                                    }else{
                                                        $display_address .= '';
                                                    }

                                                else:
                                                    $cities = array();
                                                    foreach ($addresses as $add)
                                                        if (!empty($add['city']))
                                                            $cities[] = ucfirst($add['city']);
                                                    $cities = array_unique($cities);
                                                    sort($cities);
                                                    $display_address = 'Various Locations in '.implode(', ',$cities);
                                                endif;
                                                ?>
                                                <?php echo $display_address;?>
                                            </div>
                                        <?php endif; ?>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <?php
                        $i++;
                    endforeach;

                    if($pagination):?>
                        <div class="wtd_listing_sc_pagination"><?php
                        echo $pagination;?>
                        </div><?php
                    endif;
                else:?>
                    No listings of this type are available.<?php
                endif;?>
            </div>
            <?php
            $content = ob_get_clean();
        endif;
        return $content;
    }


}

