class wtd_dining_page{

    public function __construct(){

        $wtd_plugin = get_option('wtd_plugin');
        //Page Content
        if (!empty($wtd_plugin['dining-page']))
            add_action('the_content', array(&$this, 'page_content'), 99);

        //Meta Boxes
        add_action('add_meta_boxes',array(&$this,'add_meta_box'));


        //Ajax calls
        add_action('wp_ajax_get_dinings',array(&$this,'get_dinings'));
        add_action('wp_ajax_nopriv_get_dinings',array(&$this,'get_dinings'));

        add_filter('the_content','wpautop');
    }

    public function page_content($content){

        if(!is_singular('page') || !in_the_loop())
            return $content;

        remove_filter ('the_content', 'wpautop');
        global $wtd_plugin;
        global $post;
        global $wp_query;
        $res_id = get_post_meta($post->ID,'res_id',true);
        $wtd_pages = get_option('wtd_pages');
        if(!empty($wtd_pages['dining_pages']))
            if (in_array($post->ID,$wtd_pages['dining_pages'])){


                switch ($wtd_plugin['dining_page_type']):

                    case 1:
                        $content = $this->big_menu($content);
                        break;
                    case 2:
                        $content = $this->results($content);
                        break;
                    case 3:
                        $content = $this->results($content);
                        break;

                endswitch;


            }else{
                add_filter('the_content', 'wpautop');
            }

        return $content;
    }

