use kamranahmedse\Geocode;

class wtd_connector{

    private $base_request;
    private $options;
    private $needed_vendors = array();
    private $needed_locations = array();
    private $not_on = array();
    private $paid_resorts = array();

    public function __construct()
    {
        $this->options = get_option('wtd_plugin');
        $this->options['wtd_api_key'] = get_option('wtd_api_key');
        $client_info = get_option('wtd_client_details');
        $this->paid_resorts = $client_info['resorts_available'];

        add_action('wp_ajax_wtd_check_email', array(&$this, 'check_email'));
        add_action('wp_ajax_wtd_lost_key', array(&$this, 'lost_key'));

        add_action('wp_ajax_wtd_geocode_address',array(&$this,'geocode_address'));

    }

    private function prepare_request(){
        global $wpdb;
        //Token
        $this->base_request = array(
            'token' => get_option('wtd_api_token'),
            'plugin_version' => WTD_VERSION
        );

        //Selected Resorts
        if (!empty($this->options['wtd_resorts'])){
            $this->base_request['resorts'] = array();
            foreach ($this->options['wtd_resorts'] as $resort){
                $this->base_request['resorts'][] = $wpdb->get_var($wpdb->prepare('SELECT wtd_term_id FROM '.$wpdb->prefix.'wtd_meta WHERE meta_key = "res_id" AND term_id = %s LIMIT 1',$resort));
            }
        }

        //Excluded locations
        if (!empty($this->options['excluded_locations'])){
            $this->base_request['excluded_locations'] = array();
            foreach ($this->options['excluded_locations'] as $loc){
                $this->base_request['excluded_locations'][] = $wpdb->get_var(
                    $wpdb->prepare(
                        'SELECT wtd_term_id FROM '.$wpdb->prefix.'wtd_meta WHERE term_id = %s AND meta_key = "loc_id" LIMIT 1',
                        $loc
                    )
                );
            }
        }

        //Vendors Options
        if (!empty($this->options['activity_providers'])){
            switch ($this->options['activity_providers']):

                case 2:
                    $in = '';
                    if (!empty($this->options['excluded_vendors'])){
                        $vend_count = count($this->options['excluded_vendors']);
                        $sql = 'SELECT DISTINCT meta_value FROM '.$wpdb->postmeta.' WHERE post_id IN ('.implode(',',$this->options['excluded_vendors']).') AND meta_key = "vend_id" LIMIT '.$vend_count;
                        $vendors = $wpdb->get_results($sql,ARRAY_A);
                        $this->base_request['excluded_vendors'] = array();
                        foreach ($vendors as $val)
                            $this->base_request['excluded_vendors'][] = $val['meta_value'];
                    }
                    break;
                case 3:
                    $in = '';
                    if (!empty($this->options['vendors'])){
                        $vend_count = count($this->options['vendors']);
                        $sql = 'SELECT DISTINCT meta_value FROM '.$wpdb->postmeta.' WHERE post_id IN ('.implode(',',$this->options['vendors']).') AND meta_key = "vend_id" LIMIT '.$vend_count;
                        $vendors = $wpdb->get_results($sql,ARRAY_A);
                        $this->base_request['vendors'] = array();
                        foreach ($vendors as $val)
                            $this->base_request['vendors'][] = $val['meta_value'];
                    }
                    break;

            endswitch;
        }

        //Categories
        $in = array();
        if (!empty($this->options['business_categories_options']['user'])){
            foreach ($this->options['business_categories_options']['user'] as $parent_id => $value){
                if (!empty($value['parent'])){
                    $in[] = $parent_id;
                    $children = $wpdb->get_results(
                        $wpdb->prepare(
                            'SELECT tt.term_id FROM '.$wpdb->term_taxonomy.' tt WHERE
                                    tt.taxonomy = "wtd_category"
                                    AND tt.parent = %s',
                            $parent_id
                        )
                    );
                    if ($children && !is_wp_error($children)){
                        foreach ($children as $child)
                            $in[] = $child->term_id;
                    }
                }else{
                    if (!empty($value['children']))
                        foreach ($value['children'] as $child_id => $child)
                            $in[] = $child_id;
                }
            }
        }


        if (!empty($in)){
            $in = implode(',',$in);
            $in = '('.$in.')';
            $not_ids = $wpdb->get_results(
                'SELECT wtd_term_id FROM '.$wpdb->prefix.'wtd_meta WHERE term_id IN '.$in.' AND meta_key = "cat_id"'
            );
            $ids = $wpdb->get_results(
                'SELECT wtd_term_id FROM '.$wpdb->prefix.'wtd_meta WHERE term_id NOT IN '.$in.' AND meta_key = "cat_id"'
            );
            if (!empty($not_ids)){
                $this->base_request['excluded_categories'] = array();
                foreach ($not_ids as $id)
                    $this->base_request['excluded_categories'][] = $id->wtd_term_id;
            }
        }else{
            $ids = $wpdb->get_results(
                'SELECT wtd_term_id FROM '.$wpdb->prefix.'wtd_meta WHERE meta_key = "cat_id"'
            );
            $this->base_request['excluded_categories'] = array();
        }

        if(!empty($ids)){
            $this->base_request['categories'] = array();
            foreach ($ids as $id)
                $this->base_request['categories'][] = $id->wtd_term_id;
        }

        if (!empty($this->options['business_category']))
            $this->base_request['website_type'] = $this->options['business_category'];


    }

    public function get_base_request(){

        $this->prepare_request();
        return $this->base_request;

    }

    public function update_distance($post_id){
        global $wpdb;
        $vendor_metas = $wpdb->get_results(
            $wpdb->prepare(
                'SELECT pm.* FROM '.$wpdb->postmeta.' pm
						JOIN '.$wpdb->postmeta.' pmm ON pmm.post_id = %s AND pmm.meta_key = "vend_id"
						JOIN '.$wpdb->prefix.'wtd_meta wm ON wm.meta_key = "vend_id" AND wm.wtd_term_id = pmm.meta_value
						JOIN '.$wpdb->posts.' p ON p.ID = wm.term_id
					WHERE
						pm.post_id = wm.term_id
						AND
						pm.meta_key IN ("lat","lng")
						',
                $post_id
            )
        );
        if($vendor_metas){
            foreach ($vendor_metas as $vend_data){
                $name = $vend_data->meta_key;
                $$name = $vend_data->meta_value;
            }

            $origin_lng = (!empty($this->options['user_map']['lng'])) ? $this->options['user_map']['lng'] : 38;
            $origin_lat = (!empty($this->options['user_map']['lat'])) ? $this->options['user_map']['lat'] : -95.677068;

            if (empty($lat) || empty($lng))
                $distance = 9999999999;
            else
                $distance = 3959 * acos( cos(deg2rad( $origin_lat )) * cos(deg2rad($lat)) * cos( deg2rad($lng) - deg2rad( $origin_lng ) + sin(deg2rad( $origin_lat )) * sin(deg2rad($lat))));

            update_post_meta($post_id,'distance',$distance);
        }
    }

    public function check_email(){

        $url = WTD_API_URL.'check_new_email';
        $request = array('email' => $_POST['email']);
        die($this->curl($url,$request));

    }

    public function new_client($post){
        $url = WTD_API_URL.'create_new_client';
        $json = $this->curl($url,$post);
        $response = json_decode($json);
        if ($response->status){
            ?>
            <script>
                window.location = "<?php echo admin_url('admin.php?page=wtd_plugin_settings&connect_wtd_api=1&api_key=' . $response->response->api_key);?>";
            </script>
            <style>
                html,body{
                    display:none !important;
                }
            </style>
        <?php
        }else{
            global $wtd_client_error;
            $wtd_client_error = $response->messages[0];
        }
    }

    public function lost_key(){

        $url = WTD_API_URL.'lost_key';
        $request = array(
            'email' => $_POST['email'],
            'logo_url' => WTD_PLUGIN_URL.'assets/img/whattodo-logo.png',
            'admin_page' => admin_url('admin.php?page=wtd_plugin_settings')
        );
        die($this->curl($url,$request));

    }


    public function first_touch($api_key){

        //Connect To API
        $url = WTD_API_URL.'first_touch';
        $params = array(
            'site_url' => get_home_url(),
            'site_name' => get_bloginfo('name'),
            'api_key' => $api_key,
            'redirect' => admin_url('/admin.php?page=wtd_plugin_settings&api_back=1'),
            'plugin_version' => WTD_VERSION
        );
        $this->options['wtd_api_key'] = $api_key;
        $json = $this->curl($url,$params);
        $response = json_decode($json);
        if ($response){
            if ($response->status)
            {
                update_option('wtd_api_token',$response->response->token);
                update_option('wtd_api_key',$api_key);
                update_option('wtd_client_details',(array)$response->response->client_details);
                //Import Resorts
                foreach ($response->response->resorts as $resort){
