    public function map_icons(){

        global $wpdb;

        $wpdb->query('DELETE FROM '.$wpdb->prefix.'wtd_meta WHERE meta_key = "map_icon"');

        $icons = json_decode(file_get_contents(WTD_PLUGIN_URL.'assets/maps/data.json'));
        foreach ($icons as $icon){
            $check_icon = $wpdb->get_results(
                $wpdb->prepare(
                    'SELECT * FROM '.$wpdb->prefix.'wtd_meta WHERE wtd_term_id = %s AND meta_key = "map_icon" LIMIT 1',
                    $icon->cat_id
                )
            );
            if (empty($check_icon)){
                $term_id = $wpdb->get_var(
                    $wpdb->prepare(
                        'SELECT term_id FROM '.$wpdb->prefix.'wtd_meta WHERE wtd_term_id = %s AND meta_key = "cat_id" LIMIT 1',
                        $icon->cat_id
                    )
                );
                if (!empty($term_id))
                    $wpdb->insert(
                        $wpdb->prefix.'wtd_meta',
                        array(
                            'term_id' => $term_id,
                            'wtd_term_id' => $icon->cat_id,
                            'meta_key' => 'map_icon',
                            'meta_value' => $icon->icon
                        )
                    );
            }
        }

        $no_icons = $wpdb->get_results(
            'SELECT wm.* FROM '.$wpdb->prefix.'wtd_meta wm WHERE wm.meta_key = "cat_id" AND
					(SELECT COUNT(*) FROM '.$wpdb->prefix.'wtd_meta wtdm WHERE wtdm.meta_key = "map_icon" AND wtdm.wtd_term_id = wm.wtd_term_id) = 0'
        );
        if (!empty($no_icons)){
            foreach ($no_icons as $row){
                $wpdb->insert(
                    $wpdb->prefix.'wtd_meta',
                    array(
                        'meta_key' => 'map_icon',
                        'wtd_term_id' => $row->wtd_term_id,
                        'term_id' => $row->term_id,
                        'meta_value' => WTD_PLUGIN_URL.'assets/img/map_icons/wtd_icon.png'
                    )
                );
            }
        }

    }

