    public function update($feed){
        global $wpdb;

        if (empty($this->base_request))
            $this->prepare_request();

        if (!substr_count(ini_get("disable_functions"),'set_time_limit'))
            set_time_limit(0);
        $url = WTD_API_URL.'short_update';
        $request['feed'] = $feed;
        $json = $this->curl($url,array_merge($this->base_request,$request));
        $response = json_decode($json);
        $mapped_categories = get_option('wtd_mapped_categories');

        if ($response)
            if ($response->status){
                foreach ((array)$response->response as $key => $val){
                    switch($key):
                        case 'activities':
                            foreach ($val->new as $activity)
                                $this->new_activity($activity);
                            foreach ($val->updated as $activity)
                                $this->update_activity($activity);
                            if (!empty($val->deleted)) {
                                $deleted_ids = $wpdb->get_results(
                                    'SELECT post_id FROM ' . $wpdb->prefix . 'postmeta WHERE meta_key = "list_id" AND meta_value IN (' . implode(',', $val->deleted) . ')'
                                );

                                foreach ($deleted_ids as $deleted) {
                                    wp_delete_post($deleted->post_id, true);
                                }
                            }
                            $mapped_categories = get_option('wtd_mapped_categories');
                            if (!empty($mapped_categories))
                                $mapped_categories['activities'] = $this->category_mapping('wtd_activity');
                            else
                                $mapped_categories = array(
                                    'activities' => $this->category_mapping('wtd_activity')
                                );

                            break;
                        case 'dining':
                            foreach ($val->new as $activity)
                                $this->new_activity($activity);
                            foreach ($val->updated as $activity)
                                $this->update_activity($activity);

                            if (!empty($val->deleted)) {
                                $deleted_ids = $wpdb->get_results(
                                    'SELECT post_id FROM ' . $wpdb->prefix . 'postmeta WHERE meta_key = "list_id" AND meta_value IN (' . implode(',', $val->deleted) . ')'
                                );

                                foreach ($deleted_ids as $deleted) {
                                    wp_delete_post($deleted->post_id, true);
                                }
                            }
                            break;
                        case 'coupons':
                            foreach ($val->new as $coupon)
                                $this->new_coupon($coupon);
                            foreach ($val->updated as $coupon)
                                $this->update_coupon($coupon);
                            if (!empty($val->deleted)) {
                                $deleted_ids = $wpdb->get_results(
                                    'SELECT post_id FROM ' . $wpdb->prefix . 'postmeta WHERE meta_key = "coup_id" AND meta_value IN (' . implode(',', $val->deleted) . ')'
                                );

                                foreach ($deleted_ids as $deleted) {
                                    wp_delete_post($deleted->post_id, true);
                                }
                            }

                            if (!empty($mapped_categories))
                                $mapped_categories['coupons'] = $this->category_mapping('wtd_coupon');
                            else
                                $mapped_categories = array(
                                    'coupons' => $this->category_mapping('wtd_coupon')
                                );

                            break;
                        case 'events':
                            foreach ($val->new as $event)
                                $this->new_event($event);
                            foreach ($val->updated as $event)
                                $this->update_event($event);
                            if (!empty($val->deleted)) {
                                $deleted_ids = $wpdb->get_results(
                                    'SELECT post_id FROM ' . $wpdb->prefix . 'postmeta WHERE meta_key = "eve_id" AND meta_value IN (' . implode(',', $val->deleted) . ')'
                                );

                                foreach ($deleted_ids as $deleted) {
                                    wp_delete_post($deleted->post_id, true);
                                }
                            }

                            if (!empty($mapped_categories))
                                $mapped_categories['events'] = $this->category_mapping('wtd_event');
                            else
                                $mapped_categories = array(
                                    'coupons' => $this->category_mapping('wtd_event')
                                );

                            break;
                        case 'specials':
                            foreach ($val->new as $event)
                                $this->new_special($event);
                            foreach ($val->updated as $event)
                                $this->update_event($event,true);
                            if (!empty($val->deleted)){
                                $deleted_ids = $wpdb->get_results(
                                    'SELECT post_id FROM ' . $wpdb->prefix . 'postmeta WHERE meta_key = "eve_id" AND meta_value IN (' . implode(',', $val->deleted) . ')'
                                );
                                foreach ($deleted_ids as $deleted) {
                                    wp_delete_post($deleted->post_id, true);
                                }
                            }

                            if (!empty($mapped_categories))
                                $mapped_categories['specials'] = $this->category_mapping('wtd_special');
                            else
                                $mapped_categories = array(
                                    'coupons' => $this->category_mapping('wtd_special')
                                );

                            break;
                    endswitch;
                }

                update_option('wtd_mapped_categories', $mapped_categories);
            }

    }

