            );
            wp_update_post($updated);
            $cat_ids = $wpdb->get_results(
                'SELECT term_id FROM ' . $wpdb->prefix . 'wtd_meta WHERE wtd_term_type = "wtd_category" and meta_key = "cat_id" AND wtd_term_id IN (' . implode(',', $activity->categories) . ')'
            );
            if (!empty($cat_ids)) {
                $categories = array();
                foreach ($cat_ids as $cat_id)
                    $categories[] = $cat_id->term_id;
            }
            if (!empty($categories))
                wp_set_post_terms($post_id, $categories, 'wtd_category');


            $res_id = $wpdb->get_var(
                $wpdb->prepare(
                    'SELECT term_id FROM ' . $wpdb->prefix . 'wtd_meta WHERE wtd_term_type = "wtd_resort" and meta_key = "res_id" AND wtd_term_id = %s LIMIT 1',
                    $activity->res_id
                )
            );
            wp_set_post_terms($post_id, array($res_id), 'wtd_resort');
            $info = (array)$activity;
            foreach ($info as $key => $value) {
                if (is_array($value)) {
                    $info[$key] = (array)$value;
                    foreach ($info[$key] as $k => $v)
                        $info[$key][$k] = (array)$v;
                } elseif (is_object($value))
                    $info[$key] = (array)$value;
            }

            $query = 'UPDATE ' . $wpdb->postmeta . '
    SET meta_value = CASE meta_key' . "\n";
            foreach ($info as $key => $value) {

                if (!in_array($key, array('listing_title', 'listing_about', 'categories','res_id'))) {
                    if (!empty($value)) {
                        $value = maybe_serialize($value);
                        $query .= "     WHEN '" . $key . "' THEN %s\n";
                        $values[] = $value;
                    }

                }
            }
            $query .= " END
WHERE post_id = " . $post_id;
            $query = $wpdb->prepare($query,$values);
            $wpdb->query($query);
        }else{
            $this->new_activity($activity);
        }
    }

