    private function new_category($cat){
        global $wpdb;
        global $wtd_plugin;
        $check_parent = get_term_by('name',$cat->parent->cat_name,'wtd_category');
        if (!is_wp_error($check_parent) || !$check_parent){
            $parent = wp_insert_term(
                $cat->parent->cat_name,
                'wtd_category',
                array(
                    'description' => $cat->parent->cat_about
                )
            );
            if (!is_wp_error($parent)){
                $parent_id = $parent['term_id'];
                $wpdb->insert(
                    $wpdb->prefix.'wtd_meta',
                    array(
                        'term_id' => $parent_id,
                        'wtd_term_id' => $cat->parent->cat_id,
                        'wtd_term_type' => 'wtd_category',
                        'meta_key' => 'cat_id'
                    )
                );
                if (!empty($cat->parent->cat_img))
                    $wpdb->insert(
                        $wpdb->prefix.'wtd_meta',
                        array(
                            'term_id' => $parent_id,
                            'wtd_term_id' => $cat->parent->cat_id,
                            'wtd_term_type' => 'wtd_category',
                            'meta_key' => 'cat_img',
                            'meta_value' => $cat->parent->cat_img
                        )
                    );
                if ($cat->parent->cat_default_on == 'N'){
                    $wtd_plugin['business_categories_options']['user'][$parent_id]['parent'] = 1;
                    update_option('wtd_plugin',$wtd_plugin);
                }


            }
        }else{
            $parent_id = $check_parent->term_id;
        }
        if (!empty($parent_id))
            foreach ($cat->children as $child){
                $check_kid = get_term_by('name',$child->cat_name,'wtd_category');
                if (!is_wp_error($check_kid) || !$check_kid){
                    $kid = wp_insert_term(
                        $child->cat_name,
                        'wtd_category',
                        array(
                            'description' => $child->cat_about,
                            'parent' => $parent_id
                        )
                    );
                    if (!is_wp_error($kid)){
                        $wpdb->insert(
                            $wpdb->prefix.'wtd_meta',
                            array(
                                'term_id' => $kid['term_id'],
                                'wtd_term_id' => $child->cat_id,
                                'wtd_term_type' => 'wtd_category',
                                'meta_key' => 'cat_id'
                            )
                        );
                        if (!empty($child->cat_img))
                            $wpdb->insert(
                                $wpdb->prefix.'wtd_meta',
                                array(
                                    'term_id' => $kid['term_id'],
                                    'wtd_term_id' => $child->cat_id,
                                    'wtd_term_type' => 'wtd_category',
                                    'meta_key' => 'cat_img',
                                    'meta_value' => $child->cat_img
                                )
                            );
                        if ($child->cat_default_on == 'N'){
                            $wtd_plugin['business_categories_options']['user'][$parent_id]['children'][$kid['term_id']] = 1;
                            update_option('wtd_plugin',$wtd_plugin);
                        }
                    }
                }
                else
                    wp_update_term($check_kid->term_id,'wtd_category',array('parent' => $parent_id));
            }
    }

