    private function wtd_decryptor($encrypted){
//        die($encrypted);
        if (!empty($this->options['wtd_api_key'])){
            $api_key = $this->options['wtd_api_key'];
            $encrypted = trim(mcrypt_decrypt(MCRYPT_RIJNDAEL_256, $api_key, base64_decode($encrypted), MCRYPT_MODE_ECB, mcrypt_create_iv(mcrypt_get_iv_size(MCRYPT_RIJNDAEL_256, MCRYPT_MODE_ECB), MCRYPT_RAND)));
        }
//        $encrypted = json_decode($encrypted);
//        var_dump($encrypted);
//        die();
        return $encrypted;
    }

    public function curl($url,$param = false){
        $ch = curl_init();

        $timeout = 30;

        $agents = array(
            'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/36.0.1985.67 Safari/537.36'

        );
        curl_setopt($ch,CURLOPT_USERAGENT,$agents[array_rand($agents)]);

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt ($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT,         300);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT,  $timeout );
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_VERBOSE, true);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

        if ($param)
        {
            curl_setopt ($ch, CURLOPT_POST, 1);
            curl_setopt ($ch,CURLOPT_POSTFIELDS,http_build_query($param));
        }
        else
            curl_setopt ($ch, CURLOPT_POST, 0);



        $result = curl_exec($ch);
        $data = curl_getinfo($ch);
        curl_close($ch);
        return $this->wtd_decryptor($result);
        //~ return $result;
    }


    public function category_mapping($term){
        global $wpdb;

        //Coupons
        $page_limit = 10;
        $page = 0;
        $sql = $wpdb->prepare(
            'SELECT tt.term_id,t.name,tt.parent,tt.count,t.slug,tt.taxonomy FROM '.$wpdb->terms.' t
                RIGHT JOIN '.$wpdb->term_taxonomy.' tt ON (tt.term_id = t.term_id)
                RIGHT JOIN '.$wpdb->term_relationships.' tr ON (tr.term_taxonomy_id = tt.term_taxonomy_id)
                RIGHT JOIN '.$wpdb->posts.' p ON (p.ID = tr.object_id)
            WHERE
                p.post_type = %s
                AND tt.taxonomy = "wtd_category"
            GROUP BY t.term_id
            ORDER BY t.name ASC
            LIMIT ',
            $term
        );

        $categories = array();
        $temp_categories = $wpdb->get_results($sql.$page.','.$page_limit);
        $categories = array_merge($categories,$temp_categories);
        while(!empty($temp_categories)){
            $page += $page_limit;
            $temp_categories = $wpdb->get_results($sql.$page.','.$page_limit);
            $categories = array_merge($categories,$temp_categories);
        }

        $parent_cats = array();
        foreach ($categories as $c){
            if (!$c->parent){
                if (empty($parent_cats[$c->term_id])) {
                    $parent_cats[$c->term_id] = array(
                        'parent' => $c,
                        'children' => array()
                    );
                }elseif(empty($parent_cats[$c->term_id]['parent'])){
                    $parent_cats[$c->term_id]['parent'] = $c;
                }
            }else{

                if (empty($parent_cats[$c->parent])) {
                    $parent_cats[$c->parent] = array(
                        'parent' => array(),
                        'children' => array( $c )
                    );
                }elseif(empty($parent_cats[$c->parent]['children'])){
                    $parent_cats[$c->term_id]['children'] = array( $c );
                }elseif(!empty($parent_cats[$c->parent]['children'])){
                    $parent_cats[$c->term_id]['children'][] = $c;
                }

            }
        }
        foreach ($parent_cats as $parent_id => $parent_cat){
            if (empty($parent_cat['parent'])) {
                $parent = get_term($parent_id, 'wtd_category');
                if ($parent->parent) {
                    foreach ($parent_cat['children'] as $child)
                        $parent_cats[$parent->parent]['children'][] = $child;
                    unset($parent_cats[$parent_id]);
                }
                else
                    $parent_cats[$parent_id]['parent'] = $parent;
            }
        }

        $sorted = array();
        foreach($parent_cats as $key => $p)
            $sorted[$key] = $p['parent']->name;

        asort($sorted);


        foreach ($sorted as $key => $s)
            $sorted[$key] = $parent_cats[$key];

        return $sorted;

    }

    public function decrypt_parse_response($data){
        if (!empty($this->options['wtd_api_key'])){
            $api_key = substr($this->options['wtd_api_key'],0,16);
            $iv = '1234567890123456';
            $data = mcrypt_decrypt(
                MCRYPT_RIJNDAEL_128,
                $api_key,
                hex2bin($data),
                MCRYPT_MODE_CBC,
                $iv
            );
            $pos = max(strrpos($data,']'),strrpos($data,'}'));
            $data = substr($data,0,$pos + 1);
            $blocksize = 16;
            $len = mb_strlen($data);
            $pad = ord( $data[$len - 1] );
            if ($pad && $pad < $blocksize) {
                $pm = preg_match('/' . chr( $pad ) . '{' . $pad . '}$/', $data);
                if( $pm ) {
                    $data = mb_substr($data, 0, $len - $pad);
                }
            }
        }

        return json_decode($data);
    }

